// SHOW AND HIDE BUTTON CLICK UP BUTTON
$(window).scroll(function () {
    if ($(this).scrollTop() > 50) {
        $('.js-up').css('visibility', 'visible');
    } else {
        $('.js-up').css('visibility', 'hidden');
    }
});

$('.js-up-scroll').on('click', function () {
    $('html, body').animate({scrollTop: 0}, 1000);
    return false;
});

$(".js-scroll-to-id-block").on('click', function () {
    $([document.documentElement, document.body]).animate({
        scrollTop: $("#this-block-id-to-scroll").offset().top
    }, 1000);
});

$(".js-copy-button-class").on('click', function () {
    let $temp = $("<textarea>");
    $("body").append($temp);
    $temp.val($("#copy-text-this-id").text()).select();
    document.execCommand("copy");
    $temp.remove();
});

