function popup_content(var_if) {

    let popup_1 = "<span class=\"this-content-html-popup\">this content html popup</span>";

    let popup_2 = {
        src  : '#popup-dictionary', // this id block with content html popup
        type : 'inline',
        opts : { // option fancybox
            touch: false,
            toolbar: false,
            smallBtn: false,
        }
    };

    if (var_if === 'popup-1')
        return popup_1;
    if (var_if === 'popup-2')
        return popup_2;
}

