});

$(".js-popup-2-button-show-popup").on('click', function () {
    $.fancybox.open(popup_content('popup-2'));
});

function getDatesArrayFromTo($start_date, $end_date, $format = 'Y-m-d')
{
    $array = array();
    $interval = new DateInterval('P1D');

    $realEnd = new DateTime($end_date);
    $realEnd->add($interval);

    $period = new DatePeriod(new DateTime($start_date), $interval, $realEnd);

    foreach ($period as $date) {
        $array[] = $date->format($format);
    }

    return $array;
}

