function get_slug_by_url($url) # get "slug" from the full URL address
{
    preg_match('/[^\/]+(?=\/$|$)/', $url, $matches);
    return str_replace('/', '', $matches[0]);
}

# зависимая функция - удалить если была обьявлена ранее 
function get_slug_by_url($url) # get "slug" from the full URL address
{
    preg_match('/[^\/]+(?=\/$|$)/', $url, $matches);
    return str_replace('/', '', $matches[0]);
}

function run_write_to_db($status = 301, $url_from, $url_to, $internal_redirect = true, $SQLI_WPDB)
{
    /**---- table columns that are passed in sql query
     * (url, match_url, match_data, regex, position, last_count, last_access, group_id, status, action_type, action_code, action_data, match_type, title)
     */


    # подключение БД вынести за пределы для единоразового подключения
    # $SQLI_WPDB = new mysqli(CUSTOM_DB_HOST, CUSTOM_DB_USER, CUSTOM_DB_PASSWORD, CUSTOM_DB_NAME, 3306);
    # if ($SQLI_WPDB->connect_errno) {
    #     # checking database connection errors
    #     echo "Не удалось подключиться к MySQL: (" . $GLOBALS['mysqli']->connect_errno . ") " . $GLOBALS['mysqli']->connect_error;
    #     $message = "Failed to connect to MySQL: (" . $GLOBALS['mysqli']->connect_errno . ") " . $GLOBALS['mysqli']->connect_error;
    #     exit();
    # }


    $table_prefix = 'utqawp_';
    $data = [];
    $slug = get_slug_by_url($url_from);
    # parameters for transmitting a redirect to the database
    $table_name = $table_prefix . 'redirection_items'; # table name in export import
    $data['url'] = strval('/' . $slug . '/');
    $data['match_url'] = strval('/' . $slug); // add (/) only at the beginning
    $data['match_data'] = '';
    $data['regex'] = 0;
    $data['position'] = 0;
    $data['last_count'] = 0;
    $data['last_access'] = '0000-00-00 00:00:00';
    $data['group_id'] = 1;
    $data['status'] = 'enabled';
    $data['match_type'] = 'url';
    $data['title'] = null;

    if ($status == 410) {
        $data['action_code'] = 410;
        $data['action_data'] = '';
        $data['action_type'] = 'error';
    } elseif ($status == 301) {
        if ($internal_redirect) {
            $slug = get_slug_by_url($url_to);
            $data['action_data'] = strval('/' . $slug . '/');
        } else {
            $data['action_data'] = strval($url_to);
        }
        $data['action_code'] = 301;
        $data['action_type'] = 'url';
    }


    # write all the data in a table swp_redirection_items
    $query = 'INSERT INTO ' . $table_name .
        ' (url, match_url, match_data, regex, position, last_count, last_access, group_id, status, action_type, action_code, action_data, match_type, title) VALUES ("'
        . $data['url'] . '","'
        . $data['match_url'] . '","'
        . $data['match_data'] . '","'
        . $data['regex'] . '","'
        . $data['position'] . '","'
        . $data['last_count'] . '","'
        . $data['last_access'] . '","'
        . $data['group_id'] . '","'
        . $data['status'] . '","'
        . $data['action_type'] . '","'
        . $data['action_code'] . '","'
        . $data['action_data'] . '","'
        . $data['match_type'] . '","'
        . $data['title'] . '");';

    $query_check = "SELECT `url` FROM {$table_name} WHERE url = '{$data['url']}'";
    $result['SQLI_WPDB_check'] = $SQLI_WPDB->query($query_check);
    if ($result['SQLI_WPDB_check']->num_rows < 1 && empty(mysqli_fetch_row($result['SQLI_WPDB_check'])[0]))
        $result['SQLI_WPDB'] = $SQLI_WPDB->query($query); // execute a database query
    else
        return "The database already has a redirect field from this URL -> {$data['url']}" . PHP_EOL;

    /**---- I write logs to a file ---**/
    if ($result['SQLI_WPDB'] == false) {
        return "add redirect {$data['action_code']} ------ false ERROR - error executing sql query --> {$data['url']}" . PHP_EOL;
    } elseif ($result['SQLI_WPDB'] == true) {
        if ($data['action_code'] == 410)
            return "add redirect {$data['action_code']} ------ SUCCESSFULLY WRITTEN --> {$data['url']}" . PHP_EOL;
        elseif ($data['action_code'] == 301)
            return "add redirect {$data['action_code']} ------ SUCCESSFULLY WRITTEN --> {$data['url']} redirect to --> {$data['action_data']}" . PHP_EOL;
    }
    # I free the memory occupied by the request to the database (at the moment there is no pulling out from byza but only writing) is necessary only when pulling data from their BD
//    mysqli_free_result($result);
}

function crop_text_by_chars($string = '', $start = 0, $max_value = 130)
{
    $string = strip_tags($string);
    $string = substr($string, $start, $max_value);
    $string = rtrim($string, "!,.-");
    $string = substr($string, $start, strrpos($string, ' '));
    return $string;
}

/**
 * text translation function via google translation
 * ---------------------------------------------------------------------------------------------------------------------
 */
function google_translate($text, $lang_input, $lang_uotput)
{
    $query_data = array(
        'client' => 'x',
        'q' => $text,
        'sl' => $lang_input,
        'tl' => $lang_uotput
    );
    $filename = 'http://translate.google.ru/translate_a/t';
    $options = array(
        'http' => array(
            'user_agent' => 'Mozilla/5.0 (Windows NT 6.0; rv:26.0) Gecko/20100101 Firefox/26.0',
            'method' => 'POST',
            'header' => 'Content-type: application/x-www-form-urlencoded',
            'content' => http_build_query($query_data)
        )
    );
    $context = stream_context_create($options);
    $response = file_get_contents($filename, false, $context);
    return json_decode($response);
}
